const { PromptTemplate } = require('@langchain/core/prompts');

const documentContent = $input.item.json?.data || $input.item.json?.text;
const maxChunkSize = 1000;
const minChunkSize = 400;

if (!documentContent) {
    throw new Error('No document found in input');
}

const llm = await this.getInputConnectionData('ai_languageModel', 0);

function cleanText(text) {
    return text.replace(/\s+/g, ' ').trim();
}

const chunks = [];
let remainingText = cleanText(documentContent);
let chunkNumber = 1;

if (remainingText.length <= maxChunkSize) {
    chunks.push({
        content: remainingText,
        chunk: chunkNumber,
        chunk_size: remainingText.length
    });
} else {
    while (remainingText) {
        const textToAnalyze = remainingText.substring(0, maxChunkSize);

        const promptText = `You are analyzing a document to find the best transition point to split it into meaningful sections.

Your goal: Keep related content together and split where topics naturally transition.

Read this text carefully and identify where one topic/section ends and another begins:

${textToAnalyze}

Find the best transition point that occurs BEFORE character position ${maxChunkSize}.

Look for:
- Section headings or topic changes
- Paragraph boundaries where the subject shifts
- Complete conclusions before new ideas start
- Natural breaks between different aspects of the content

Output the LAST WORD that appears right before your chosen split point.
Just the single word itself, nothing else.
Example: If you want to split after "The company was founded in 2022." then output: "2022"`;

        const prompt = PromptTemplate.fromTemplate(promptText);
        const chain = prompt.pipe(llm);

        let breakPoint = maxChunkSize;

        try {
            const response = await chain.invoke();
            const responseText = response.content || response.text || response.toString();
            const breakWord = responseText.trim();

            if (breakWord) {
                // Find the last occurrence of this word in the text to analyze
                const wordIndex = textToAnalyze.lastIndexOf(breakWord);
                if (wordIndex !== -1) {
                    // Split after the word (including any punctuation that follows)
                    breakPoint = wordIndex + breakWord.length;
                    // Move past any punctuation or single space after the word
                    while (breakPoint < textToAnalyze.length && 
                           (textToAnalyze[breakPoint] === '.' || 
                            textToAnalyze[breakPoint] === '!' || 
                            textToAnalyze[breakPoint] === '?' || 
                            textToAnalyze[breakPoint] === ',' || 
                            textToAnalyze[breakPoint] === ';' || 
                            textToAnalyze[breakPoint] === ':' || 
                            textToAnalyze[breakPoint] === ' ')) {
                        breakPoint++;
                        // Stop after moving past one space
                        if (textToAnalyze[breakPoint - 1] === ' ') break;
                    }
                    breakPoint = Math.min(breakPoint, maxChunkSize);
                }
            }
        } catch (error) {
            console.log('LLM failed to determine breakpoint, using max size:', error.message);
            breakPoint = maxChunkSize;
        }

        const chunk = remainingText.substring(0, breakPoint).trim();

        if (chunk) {
            chunks.push({
                content: chunk,
                chunk: chunkNumber,
                chunk_size: chunk.length
            });
            chunkNumber++;
        }

        remainingText = remainingText.substring(breakPoint).trim();

        if (!remainingText) {
            break;
        }
    }
}

// Merge chunks that are below minimum size with adjacent chunks if possible
let i = 0;
while (i < chunks.length) {
    if (chunks[i].chunk_size < minChunkSize) {
        // Try to merge with next chunk first if it exists and won't exceed max
        if (i + 1 < chunks.length && 
            chunks[i].chunk_size + chunks[i + 1].chunk_size <= maxChunkSize) {
            // Merge current with next
            chunks[i].content += ' ' + chunks[i + 1].content;
            chunks[i].chunk_size = chunks[i].content.length;
            chunks.splice(i + 1, 1);
            // Don't increment i, check this chunk again in case it's still small
        } 
        // Otherwise try to merge with previous chunk if it exists and won't exceed max
        else if (i > 0 && 
                 chunks[i - 1].chunk_size + chunks[i].chunk_size <= maxChunkSize) {
            // Merge current into previous
            chunks[i - 1].content += ' ' + chunks[i].content;
            chunks[i - 1].chunk_size = chunks[i - 1].content.length;
            chunks.splice(i, 1);
            // Don't increment i, we removed current chunk
        } else {
            // Can't merge without exceeding max, move on
            i++;
        }
    } else {
        i++;
    }
}

const returnData = chunks.map(chunk => ({
    json: chunk
}));

return returnData;

